 namespace Facade.Base {     using System.Net.Http;     using Xamarin.Forms;          public class RestService     {         /// <summary>         /// The client.         /// </summary>         public HttpClient client { get; set; }          /// <summary>         /// The URI.         /// </summary>         public string Uri { get; set; }          /// <summary>         /// The DBN ame.         /// </summary>         public string DBName { get; set; }          /// <summary>         /// Initializes a new instance of the <see cref="T:Facade.Base.RestService"/> class.         /// </summary>         public RestService(string url, string dbName)         {             this.Uri = url;             this.DBName = dbName;             this.client = new HttpClient();             this.client.MaxResponseContentBufferSize = 256000;         }          /// <summary>         /// Is the connected.         /// </summary>         /// <returns><c>true</c>, if connected was ised, <c>false</c> otherwise.</returns>         public bool IsConnected()         {             var connection = DependencyService.Get<IConnection>();             if (connection != null)             {
                return (connection.ConectadoRed() && connection.RespondeServicio(Uri));
            }             else                 return false;         }     } }
